function burgerMenu(){const e=document.querySelector(".burger"),s=document.querySelector(".menu"),i=document.querySelector("body");e.addEventListener("click",(()=>{s.classList.contains("active")?(s.classList.remove("active"),e.classList.remove("active-burger"),i.classList.remove("locked")):(s.classList.add("active"),e.classList.add("active-burger"),i.classList.add("locked"))})),window.addEventListener("resize",(()=>{window.innerWidth>991.98&&(s.classList.remove("active"),e.classList.remove("active-burger"),i.classList.remove("locked"))}))}function fixedNav(){const e=document.querySelector("nav");window.scrollY>=1?e.classList.add("fixed__nav"):e.classList.remove("fixed__nav")}burgerMenu(),window.addEventListener("scroll",fixedNav),$(document).ready((function(){$(".slider").slick({arrows:!0,slidesToShow:3,slidesToScroll:3,speed:1e3,infinite:!0,initialSlide:0,autoplay:!0,autoplaySpeed:3e3,variableWidth:!0})}));